INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-canopy-001', 'Red Oak', 'Quercus rubra', 'canopy', 1, 25, 75, 60, 'Full sun', 'Medium', 'Fast-growing oak with brilliant fall color, important wildlife tree', '["acorn_production","wildlife_habitat","shade_provider","fall_color"]', '["American Hazelnut","Serviceberry","Wild Ginger"]', 'Zone 2-3 as canopy anchor. Faster growing than white oak, good for establishing food forests quickly.', '{"acorns":"fall (after leaching)"}', 'Common at nurseries. Easier to establish than white oak.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 30, 60, 1),

('ne-canopy-002', 'Black Cherry', 'Prunus serotina', 'canopy', 1, 15, 70, 40, 'Full sun to part shade', 'Medium', 'Valuable timber tree with edible fruit, attracts birds', '["edible_fruit","wildlife_food","timber_production","early_successional"]', '["Pawpaw","Spicebush","Ferns"]', 'Zone 3-4. Pioneer species, good for establishing new food forests. Self-sows readily.', '{"cherries":"late summer"}', 'Easy from seed. Birds spread naturally. Watch for tent caterpillars.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 55, 1),

('ne-canopy-003', 'Shagbark Hickory', 'Carya ovata', 'canopy', 1, 30, 80, 50, 'Full sun', 'Medium', 'Distinctive shaggy bark, produces sweet edible nuts', '["edible_nuts","wildlife_food","timber_value","syrup_production"]', '["Black Walnut","Red Oak","Witch Hazel"]', 'Zone 3-4. Slow starter but long-lived. Excellent nut production for humans and wildlife.', '{"nuts":"fall"}', 'Difficult to transplant. Best started from seed or small container. Patience required.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 30, 60, 1),

('ne-canopy-004', 'Tulip Poplar', 'Liriodendron tulipifera', 'canopy', 1, 15, 90, 40, 'Full sun', 'Medium to wet', 'Fast-growing, tall straight tree with tulip-shaped flowers', '["timber_production","bee_forage","fast_growth","biomass_production"]', '["American Beech","Spicebush","Solomon Seal"]', 'Zone 3-4. Excellent for rapid canopy establishment. Abundant nectar for bees in late spring.', '{}', 'Fast growing. Good for timber and biomass. Prefers moist, rich sites.', '["Northeast","Mid_Atlantic","Southeast"]', '4', '9', 35, 65, 1),

('ne-canopy-005', 'American Beech', 'Fagus grandifolia', 'canopy', 1, 30, 75, 50, 'Full sun to part shade', 'Medium', 'Smooth gray bark, produces beechnuts, excellent shade tree', '["edible_nuts","wildlife_food","dense_shade","long_lived"]', '["Witch Hazel","Trout Lily","Jack-in-the-Pulpit"]', 'Zone 3-4. Creates dense shade, good for mycorrhizal networks. Nuts every 2-3 years.', '{"beechnuts":"fall"}', 'Slow growing. Hard to transplant. Creates deep shade limiting understory.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 30, 60, 1),

('ne-canopy-006', 'American Chestnut (Blight-Resistant)', 'Castanea dentata', 'canopy', 1, 15, 60, 40, 'Full sun', 'Medium', 'Blight-resistant hybrid, produces abundant sweet nuts', '["edible_nuts","wildlife_food","fast_growth","timber_potential"]', '["Red Oak","Serviceberry","Blueberry"]', 'Zone 2-3. Use blight-resistant varieties. Excellent nut production, historical importance.', '{"nuts":"fall"}', 'Seek out American Chestnut Foundation sources. Plant blight-resistant hybrids only.', '["Northeast","Mid_Atlantic"]', '4', '8', 30, 55, 1),

('ne-canopy-007', 'Black Locust', 'Robinia pseudoacacia', 'canopy', 1, 10, 60, 30, 'Full sun', 'Dry to medium', 'Nitrogen-fixing tree with fragrant flowers, rot-resistant wood', '["nitrogen_fixer","bee_forage","rot_resistant_wood","fast_growth"]', '["Elderberry","Grape","Comfrey"]', 'Zone 3-4. Excellent nitrogen fixer. Avoid near paths (thorny). Good for posts and firewood.', '{"flowers":"late spring (edible, caution advised)"}', 'Aggressive spreader via root suckers. Plant where spreading is okay. Free firewood forever.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 20, 50, 1),

('ne-canopy-008', 'Basswood / American Linden', 'Tilia americana', 'canopy', 1, 20, 70, 50, 'Full sun to part shade', 'Medium', 'Fragrant flowers beloved by bees, heart-shaped leaves', '["bee_forage","medicinal_flowers","carving_wood","dense_shade"]', '["Redbud","Spicebush","Wild Ginger"]', 'Zone 2-3. Exceptional bee tree. Flowers make calming tea. Coppices well.', '{"flowers":"late spring (tea)"}', 'Easy to grow. Basswood honey is prized. Can be coppiced for cordage fiber.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 30, 60, 1),

('ne-canopy-009', 'Black Walnut', 'Juglans nigra', 'canopy', 1, 15, 75, 50, 'Full sun', 'Medium', 'Valuable timber and nut tree, juglone affects nearby plants', '["edible_nuts","timber_value","wildlife_food","allelopathic"]', '["Black Locust","Elderberry","Pawpaw (resistant)"]', 'Zone 3-4. Keep away from juglone-sensitive plants. High-value timber and nuts.', '{"nuts":"fall"}', 'Choose juglone-tolerant companions. Nuts ready when husks crack. Valuable long-term investment.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 30, 60, 1),

('ne-canopy-010', 'American Sycamore', 'Platanus occidentalis', 'canopy', 1, 20, 80, 60, 'Full sun', 'Wet to medium', 'Massive tree with distinctive peeling bark, loves water', '["riparian_buffer","erosion_control","wildlife_cavities","biomass"]', '["Elderberry","Witch Hazel","Cardinal Flower"]', 'Zone 4-5. Excellent for wet areas and stream banks. Provides nesting cavities.', '{}', 'Plant near water. Tolerates flooding. Very fast growth in wet sites.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 35, 65, 1),

('ne-canopy-011', 'Eastern White Pine', 'Pinus strobus', 'canopy', 1, 20, 80, 40, 'Full sun', 'Medium', 'Soft needles, straight trunk, important wildlife cover', '["windbreak","wildlife_shelter","timber_production","evergreen_cover"]', '["Blueberry","Wintergreen","Bunchberry"]', 'Zone 3-4. Creates acidic soil. Good windbreak. Pine nuts edible but tiny.', '{"inner_bark":"spring (survival food)","nuts":"fall (small)"}', 'Fast growing evergreen. Prefers well-drained soil. Can get white pine blister rust.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 25, 55, 1),

('ne-canopy-012', 'Eastern Hemlock', 'Tsuga canadensis', 'canopy', 1, 25, 70, 35, 'Part shade to full sun', 'Medium', 'Graceful evergreen, important for shaded streams', '["evergreen_cover","wildlife_shelter","riparian_shade","erosion_control"]', '["Rhododendron","Mountain Laurel","Ferns"]', 'Zone 3-4. Prefers cool, moist sites. Watch for woolly adelgid pest in some areas.', '{}', 'Prefers shade when young. Excellent for cool north slopes. Hemlock adelgid threat in some regions.', '["Northeast","Mid_Atlantic"]', '3', '7', 30, 60, 1),

('ne-canopy-013', 'Paper Birch', 'Betula papyrifera', 'canopy', 1, 20, 70, 35, 'Full sun', 'Medium', 'Iconic white peeling bark, pioneer species', '["wildlife_food","sap_production","pioneer_species","aesthetic"]', '["Alder","Serviceberry","Bunchberry"]', 'Zone 3-4. Short-lived pioneer. Tap for sweet sap. Beautiful white bark.', '{"sap":"early spring (syrup)"}', 'Short-lived (60-80 years). Susceptible to bronze birch borer in stressed conditions.', '["Northeast","Midwest"]', '2', '7', 25, 50, 1),

('ne-canopy-014', 'Yellow Birch', 'Betula alleghaniensis', 'canopy', 1, 25, 75, 40, 'Full sun to part shade', 'Wet to medium', 'Yellowish peeling bark, aromatic twigs', '["sap_production","timber_value","wintergreen_flavor","wildlife"]', '["Eastern Hemlock","Hobblebush","Trout Lily"]', 'Zone 3-4. Tap for birch beer sap. Twigs smell like wintergreen when scratched.', '{"sap":"early spring","twigs":"year-round (wintergreen tea)"}', 'Better timber than paper birch. Prefers cooler regions. Twigs make excellent tea.', '["Northeast","Mid_Atlantic"]', '3', '7', 30, 60, 1),

('ne-canopy-015', 'Red Maple', 'Acer rubrum', 'canopy', 1, 20, 70, 40, 'Full sun to part shade', 'Wet to medium', 'Brilliant red fall color, adapts to wet sites', '["sap_production","fall_color","wet_tolerance","early_flowers"]', '["Sweet Pepperbush","Winterberry","Joe Pye Weed"]', 'Zone 2-4. Tolerates wet soil. Early flowers feed pollinators. Tap for syrup (less sweet than sugar maple).', '{"sap":"early spring"}', 'Very adaptable. Tolerates both wet and dry. Less sugar content than sugar maple but tappable.', '["Northeast","Mid_Atlantic","Southeast","Midwest"]', '3', '9', 25, 60, 1),

('ne-canopy-016', 'Silver Maple', 'Acer saccharinum', 'canopy', 1, 15, 70, 50, 'Full sun', 'Wet to medium', 'Fast-growing maple for wet areas, brittle wood', '["sap_production","fast_growth","wet_tolerance","wildlife_seeds"]', '["Buttonbush","Swamp Milkweed","Ferns"]', 'Zone 4-5. Good for wet areas needing quick shade. Brittle branches in storms.', '{"sap":"early spring"}', 'Very fast growing. Good for floodplains. Wood is weak, expect branch drop.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 30, 60, 1),

('ne-canopy-017', 'American Elm (Disease-Resistant)', 'Ulmus americana', 'canopy', 1, 25, 80, 60, 'Full sun', 'Medium to wet', 'Classic vase-shaped shade tree, use resistant varieties', '["shade_provider","wildlife_habitat","street_tree","erosion_control"]', '["Hackberry","Redbud","Bloodroot"]', 'Zone 3-4. Use disease-resistant cultivars only. Classic American shade tree.', '{}', 'Only plant Dutch elm disease resistant varieties. Beautiful form. Tolerates urban conditions.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 60, 1),

('ne-canopy-018', 'Bur Oak', 'Quercus macrocarpa', 'canopy', 1, 30, 80, 80, 'Full sun', 'Medium', 'Massive acorns, drought tolerant, wide-spreading oak', '["acorn_production","drought_tolerant","wildlife_food","windbreak"]', '["Hazelnut","Chokecherry","Big Bluestem"]', 'Zone 3-4. Very drought tolerant. Huge acorns (sweeter than most oaks). Long-lived.', '{"acorns":"fall"}', 'Slow to start but drought tough. Acorns among sweetest of oaks. Prairie-savanna indicator.', '["Midwest","Northeast"]', '3', '8', 15, 50, 1);

