INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-understory-001', 'Pawpaw', 'Asimina triloba', 'understory', 1, 5, 20, 15, 'Part shade to full sun', 'Medium', 'Native tropical fruit, custard-like flavor, largest native fruit', '["edible_fruit","understory_crop","unique_flavor","wildlife_resistant"]', '["Black Cherry","Spicebush","Solomon Seal"]', 'Zone 1-2 for harvest. Needs cross-pollination (plant 2+). Tolerates shade when young, fruits best in sun.', '{"fruit":"late summer to fall"}', 'Plant 2+ for pollination. Fruits in 5-7 years from graft, 7-10 from seed. Fruit doesnt ship well, local only.', '["Northeast","Mid_Atlantic","Midwest"]', '5', '9', 30, 60, 1),

('ne-understory-002', 'American Hawthorn', 'Crataegus spp.', 'understory', 1, 5, 20, 20, 'Full sun to part shade', 'Medium', 'Thorny small tree with edible haws, medicinal', '["edible_fruit","medicinal","wildlife_food","thorny_barrier"]', '["Wild Rose","Elderberry","Hazelnut"]', 'Zone 2-3. Excellent hedge/barrier. Fruits and flowers medicinal (heart health).', '{"fruit":"fall","flowers":"spring (tea)"}', 'Many native species. Thorny - good living fence. Haws make jelly. Important medicine.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 55, 1),

('ne-understory-003', 'Persimmon', 'Diospyros virginiana', 'understory', 1, 5, 30, 25, 'Full sun', 'Medium', 'Orange fruits extremely sweet when fully ripe', '["edible_fruit","wildlife_food","fall_harvest","unique_flavor"]', '["Pawpaw","Sassafras","Sumac"]', 'Zone 2-3. Wait for frost before harvest (very astringent when unripe). Plant male and female.', '{"fruit":"late fall after frost"}', 'Need male and female for fruit. Fruits after frost eliminates astringency. Amazing flavor.', '["Northeast","Mid_Atlantic","Southeast","Midwest"]', '5', '9', 25, 55, 1),

('ne-understory-004', 'Flowering Dogwood', 'Cornus florida', 'understory', 1, 7, 25, 25, 'Part shade', 'Medium', 'Showy spring flowers, red berries, brilliant fall color', '["ornamental","wildlife_fruit","spring_flowers","fall_color"]', '["Redbud","Azalea","Ferns"]', 'Zone 1-2. Prefers acidic soil. Beautiful four seasons. Berries for birds in fall.', '{"berries":"fall (for wildlife)"}', 'Prefers acidic soil. Watch for anthracnose in humid areas. Shallow roots, mulch well.', '["Northeast","Mid_Atlantic","Southeast"]', '5', '9', 30, 60, 1),

('ne-understory-005', 'Sassafras', 'Sassafras albidum', 'understory', 1, 5, 30, 25, 'Full sun to part shade', 'Medium', 'Aromatic roots for tea, unique mitten-shaped leaves', '["aromatic","root_tea","fall_color","pioneer_species"]', '["Persimmon","Sumac","Blackberry"]', 'Zone 2-3. Spreads by root suckers. Root bark makes tea (file powder for gumbo). All parts aromatic.', '{"roots":"fall (tea)","leaves":"spring (file powder)"}', 'Suckers freely - good or bad depending on context. Root tea (safrole concern, use in moderation).', '["Northeast","Mid_Atlantic","Southeast","Midwest"]', '4', '9', 25, 55, 1),

('ne-understory-006', 'Witch Hazel', 'Hamamelis virginiana', 'understory', 1, 5, 20, 15, 'Part shade to full sun', 'Medium', 'Late fall flowers, medicinal bark, unique bloom time', '["medicinal","late_flowers","understory_crop","wildlife"]', '["Beech","Spicebush","Ferns"]', 'Zone 2-3. Blooms October-November. Bark used for astringent. Shade tolerant.', '{"bark":"fall (medicinal extract)"}', 'Commercially harvested for witch hazel extract. Easy to grow. Tolerates shade well.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 30, 60, 1),

('ne-understory-007', 'Ironwood / Hornbeam', 'Carpinus caroliniana', 'understory', 1, 10, 30, 25, 'Part shade to full sun', 'Medium', 'Muscular trunk, extremely hard wood, shade tolerant', '["dense_wood","wildlife","shade_tolerant","long_lived"]', '["Witch Hazel","Jack-in-the-Pulpit","Christmas Fern"]', 'Zone 3-4. Very shade tolerant. Slow growing but long-lived. Beautiful bark.', '{}', 'Slow growing. Extremely hard wood. Good for shaded woodland gardens.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 60, 1),

('ne-understory-008', 'Hop Hornbeam', 'Ostrya virginiana', 'understory', 1, 10, 30, 20, 'Part shade to full sun', 'Dry to medium', 'Similar to ironwood, hop-like seed clusters', '["dense_wood","shade_tolerant","unique_seeds","wildlife"]', '["Serviceberry","Wild Geranium","Ferns"]', 'Zone 3-4. Drought tolerant when established. Very hard wood. Seeds resemble hops.', '{}', 'More drought tolerant than ironwood. Very hard wood. Slow growing.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 20, 50, 1),

('ne-understory-009', 'Chokecherry', 'Prunus virginiana', 'understory', 1, 3, 20, 15, 'Full sun to part shade', 'Medium', 'Abundant small cherries, important wildlife food', '["edible_fruit","wildlife_food","windbreak","erosion_control"]', '["Elderberry","Hazelnut","Nannyberry"]', 'Zone 2-3. Fruits make excellent jelly. Pit is toxic, use fruit flesh only. Spreads by suckers.', '{"fruit":"late summer (flesh only, toxic pits)"}', 'Very productive. Spreads by root suckers. Make jelly from fruit flesh, discard pits (contain cyanide).', '["Northeast","Midwest"]', '2', '7', 20, 50, 1),

('ne-understory-010', 'Pin Cherry', 'Prunus pensylvanica', 'understory', 1, 3, 25, 15, 'Full sun', 'Medium', 'Small tart cherries, pioneer species, prolific producer', '["edible_fruit","pioneer_species","wildlife_food","early_successional"]', '["Black Locust","Raspberry","Fire Cherry"]', 'Zone 3-4. First tree after disturbance. Very productive. Birds love fruit.', '{"fruit":"mid summer"}', 'Short-lived pioneer (20-30 years). Self-sows readily. Abundant small fruit.', '["Northeast","Midwest"]', '2', '6', 25, 50, 1),

('ne-understory-011', 'Mountain Ash', 'Sorbus americana', 'understory', 1, 5, 25, 15, 'Full sun to part shade', 'Medium', 'Clusters of orange berries, important winter wildlife food', '["wildlife_food","ornamental","winter_interest","jam"]', '["Serviceberry","Blueberry","Mountain Laurel"]', 'Zone 2-3. Berries persist into winter. Slightly astringent raw, good cooked.', '{"fruit":"fall (cooked, bitter raw)"}', 'Prefers cooler climates. Berries important winter wildlife food. Best cooked or after frost.', '["Northeast","Midwest"]', '2', '6', 30, 60, 1),

('ne-understory-012', 'Nannyberry', 'Viburnum lentago', 'understory', 1, 3, 15, 10, 'Full sun to part shade', 'Medium to wet', 'Edible blue-black berries, tolerates wet sites', '["edible_fruit","wildlife_food","wet_tolerance","fall_color"]', '["Elderberry","Winterberry","Joe Pye Weed"]', 'Zone 2-3. Sweet berries when fully ripe. Tolerates wet sites. Good hedge.', '{"fruit":"fall"}', 'Berries sweet when very ripe. Tolerates wet soil. Makes good hedge/screen.', '["Northeast","Midwest"]', '2', '8', 25, 55, 1),

('ne-understory-013', 'Blackhaw Viburnum', 'Viburnum prunifolium', 'understory', 1, 3, 15, 12, 'Full sun to part shade', 'Medium', 'Edible berries, beautiful fall color, medicinal bark', '["edible_fruit","medicinal","fall_color","wildlife_food"]', '["Redbud","Dogwood","Hazelnut"]', 'Zone 2-3. Berries edible when very ripe. Bark traditionally used as relaxant.', '{"fruit":"fall","bark":"(medicinal - consult expert)"}', 'Similar to nannyberry but prefers drier sites. Medicinal bark (consult herbalist).', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 55, 1),

('ne-understory-014', 'Alternate-leaf Dogwood', 'Cornus alternifolia', 'understory', 1, 5, 20, 20, 'Part shade to full sun', 'Medium', 'Horizontal branching, blue-black berries', '["wildlife_fruit","ornamental","understory","unique_form"]', '["Witch Hazel","Ferns","Wild Ginger"]', 'Zone 2-3. Distinctive horizontal layers. Berries loved by birds. Shade tolerant.', '{"berries":"fall (for wildlife)"}', 'Unique pagoda-like form. Good for shaded gardens. Berries attract many bird species.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 30, 60, 1),

('ne-understory-015', 'Downy Serviceberry', 'Amelanchier arborea', 'understory', 1, 3, 25, 15, 'Full sun to part shade', 'Medium', 'Tree form of serviceberry, abundant edible berries', '["edible_fruit","pollinator_support","wildlife_food","spring_flowers"]', '["Redbud","Hazelnut","Blueberry"]', 'Zone 1-2. Larger tree form of serviceberry. Beautiful spring flowers, delicious fruit.', '{"berries":"early summer"}', 'Taller than shrubby serviceberry species. Beautiful spring bloom. Birds compete for berries.', '["Northeast","Mid_Atlantic"]', '4', '9', 30, 55, 1),

('ne-understory-016', 'Striped Maple', 'Acer pensylvanicum', 'understory', 1, 7, 20, 15, 'Part shade to full sun', 'Medium', 'Distinctive green bark with white stripes, prefers cool sites', '["ornamental","wildlife_browse","cool_climate","unique_bark"]', '["Beech","Hemlock","Hobblebush"]', 'Zone 2-3. Prefers cool, shaded sites. Moose browse. Unusual striped bark.', '{}', 'Prefers cooler mountain regions. Interesting bark pattern. Also called Moosewood.', '["Northeast"]', '3', '7', 30, 60, 1),

('ne-understory-017', 'Black Tupelo', 'Nyssa sylvatica', 'understory', 1, 7, 30, 20, 'Full sun to part shade', 'Wet to medium', 'Spectacular fall color, important bee and bird plant', '["bee_forage","wildlife_fruit","fall_color","wet_tolerance"]', '["Sweet Pepperbush","Winterberry","Clethra"]', 'Zone 2-3. Among earliest and most brilliant fall color. Flowers excellent for bees.', '{"fruit":"fall (for wildlife, sour for humans)"}', 'Difficult to transplant. Worth it for fall color. Important for bees and birds.', '["Northeast","Mid_Atlantic","Southeast"]', '4', '9', 30, 65, 1),

('ne-understory-018', 'Crabapple (Native)', 'Malus coronaria', 'understory', 1, 4, 20, 20, 'Full sun', 'Medium', 'Fragrant pink flowers, small tart apples for wildlife', '["wildlife_food","pollinator_support","jelly_making","ornamental"]', '["Hazelnut","Elderberry","Currant"]', 'Zone 2-3. Small tart apples make excellent jelly. Beautiful fragrant flowers.', '{"fruit":"fall (tart, best cooked)"}', 'Very ornamental. Fruits too tart raw but excellent cooked. Disease resistant native.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 25, 55, 1),

('ne-understory-019', 'Allegheny Serviceberry', 'Amelanchier laevis', 'understory', 1, 3, 20, 15, 'Full sun to part shade', 'Medium', 'Smooth-leaved serviceberry with excellent fruit', '["edible_fruit","pollinator_support","wildlife_food","ornamental"]', '["Redbud","Blueberry","Spicebush"]', 'Zone 1-2. Among best-tasting serviceberries. Beautiful bronze new growth.', '{"berries":"early summer"}', 'Smooth leaves distinguish from downy serviceberry. Excellent flavor. Beautiful spring bloom.', '["Northeast","Mid_Atlantic"]', '4', '8', 30, 55, 1),

('ne-understory-020', 'Pagoda Dogwood', 'Cornus alternifolia', 'understory', 1, 5, 20, 20, 'Part shade', 'Medium', 'Layered branching, shade tolerant, wildlife berries', '["wildlife_fruit","ornamental","shade_tolerant","unique_form"]', '["Viburnum","Witch Hazel","Ferns"]', 'Zone 2-3. Distinctive horizontal branching. Tolerates heavy shade.', '{}', 'Beautiful architectural form. Good for woodland gardens. Very shade tolerant.', '["Northeast","Mid_Atlantic"]', '3', '7', 30, 60, 1),

('ne-understory-021', 'Possumhaw', 'Viburnum nudum', 'understory', 1, 3, 12, 8, 'Full sun to part shade', 'Wet to medium', 'Glossy leaves, colorful berries, wet site tolerant', '["wildlife_fruit","ornamental","wet_tolerance","fall_color"]', '["Winterberry","Sweetspire","Joe Pye Weed"]', 'Zone 2-3. Berries change from white to pink to blue. Tolerates wet sites.', '{}', 'Adaptable to wet or dry. Colorful fruit display. Good wildlife value.', '["Northeast","Mid_Atlantic","Southeast"]', '5', '9', 30, 65, 1),

('ne-understory-022', 'Spicebush', 'Lindera benzoin', 'understory', 1, 3, 12, 12, 'Part shade', 'Medium to wet', 'Aromatic shrub-tree, host for swallowtail butterflies', '["spice_production","butterfly_host","aromatic","medicinal"]', '["Pawpaw","Mayapple","Jack-in-the-Pulpit"]', 'Zone 1-2. All parts aromatic. Berries dry for allspice substitute. Swallowtail host.', '{"berries":"fall (spice)","twigs":"year round (tea)"}', 'Easy to grow. Shade tolerant. Important swallowtail butterfly host plant.', '["Northeast","Mid_Atlantic"]', '4', '9', 30, 60, 1),

('ne-understory-023', 'Prickly Ash', 'Zanthoxylum americanum', 'understory', 1, 4, 15, 10, 'Full sun to part shade', 'Medium', 'Citrus family, numbing spice, medicinal bark', '["spice_production","medicinal","unique_flavor","thorny"]', '["Elderberry","Sumac","Hazelnut"]', 'Zone 2-3. Berries create tingling sensation. Used in traditional medicine.', '{"berries":"fall (spice, numbing)","bark":"(medicinal - expert use)"}', 'Also called Toothache Tree. Berries numb mouth (Sichuan pepper relative). Thorny.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '7', 25, 50, 1),

('ne-understory-024', 'American Smoketree', 'Cotinus obovatus', 'understory', 1, 5, 25, 20, 'Full sun', 'Medium', 'Rare native, stunning fall color, unique fluffy seed heads', '["ornamental","fall_color","rare_native","unique"]', '["Redbud","Sumac","Little Bluestem"]', 'Zone 2-3. Rare in wild. Spectacular fall color. Unique smoky seed plumes.', '{}', 'Rare in cultivation. Prefers limestone soils. Spectacular orange-red fall color.', '["Mid_Atlantic","Southeast"]', '4', '8', 25, 45, 1);

