-- SHRUB LAYER (3-15 feet) - Multi-stemmed woody plants
-- ---------------------------------------------------------------------------

INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-shrub-001', 'American Hazelnut', 'Corylus americana', 'shrub', 1, 3, 10, 10, 'Full sun to part shade', 'Medium', 'Native nut shrub, excellent wildlife value', '["edible_nuts","wildlife_food","nitrogen_helper","hedge"]', '["Oak","Serviceberry","Wild Ginger"]', 'Zone 1-2. Plant 2+ for pollination. Nuts before squirrels get them. Good hedge.', '{"nuts":"fall"}', 'Plant multiple for cross-pollination. Harvest before squirrels. Good windbreak.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 25, 55, 1),

('ne-shrub-002', 'Elderberry', 'Sambucus canadensis', 'shrub', 1, 2, 12, 12, 'Full sun to part shade', 'Medium to wet', 'Medicinal flowers and berries, rapid growth', '["edible_fruit","medicinal","wildlife_food","pioneer_species"]', '["Black Locust","Comfrey","Bee Balm"]', 'Zone 1-2. Flowers for fritters/tea. Berries must be cooked. Rapid growth.', '{"flowers":"early summer (cooked)","berries":"late summer (cooked only)"}', 'Easy from cuttings. Berries MUST be cooked (raw = toxic). Immune support herb.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 60, 1),

('ne-shrub-003', 'Highbush Blueberry', 'Vaccinium corymbosum', 'shrub', 1, 3, 8, 6, 'Full sun to part shade', 'Medium', 'Delicious berries, brilliant fall color, needs acidic soil', '["edible_fruit","pollinator_support","fall_color","acidic_soil"]', '["Azalea","Mountain Laurel","Wintergreen"]', 'Zone 1. Needs acidic soil. Plant multiple varieties for extended harvest.', '{"berries":"summer"}', 'Requires acidic soil. Plant multiple varieties. Excellent fresh or preserved.', '["Northeast","Mid_Atlantic"]', '3', '8', 35, 60, 1),

('ne-shrub-004', 'Lowbush Blueberry', 'Vaccinium angustifolium', 'shrub', 1, 2, 1.5, 3, 'Full sun', 'Medium', 'Groundcover blueberry, intense flavor, spreading habit', '["edible_fruit","groundcover","pollinator_support","erosion_control"]', '["Huckleberry","Wintergreen","Bearberry"]', 'Zone 1. Spreads by rhizomes. Small but intensely flavored berries.', '{"berries":"mid summer"}', 'Wild lowbush type. Spreads to form colonies. Harvest is low but flavor intense.', '["Northeast"]', '2', '7', 30, 55, 1),

('ne-shrub-005', 'American Cranberry', 'Vaccinium macrocarpon', 'shrub', 1, 2, 0.5, 6, 'Full sun', 'Wet', 'Bog plant, spreading evergreen, tart berries', '["edible_fruit","wetland_plant","evergreen","groundcover"]', '["Bog Rosemary","Leatherleaf","Pitcher Plant"]', 'Zone 1-2. Requires acidic bog conditions. Spreads by runners.', '{"berries":"fall"}', 'Requires wet acidic soil. Commercial cranberry bogs. Spreads as groundcover.', '["Northeast"]', '2', '7', 35, 60, 1),

('ne-shrub-006', 'Winterberry', 'Ilex verticillata', 'shrub', 1, 3, 10, 10, 'Full sun to part shade', 'Wet to medium', 'Deciduous holly, brilliant red winter berries', '["wildlife_food","ornamental","wet_tolerance","winter_interest"]', '["Sweet Pepperbush","Joe Pye Weed","Cardinal Flower"]', 'Zone 2-3. Need male and female for berries. Tolerates wet soil.', '{}', 'Plant male and female. Berries persist all winter. Excellent wet site plant.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 30, 65, 1),

('ne-shrub-007', 'Inkberry', 'Ilex glabra', 'shrub', 1, 3, 8, 8, 'Full sun to part shade', 'Wet to medium', 'Evergreen holly, black berries, tolerates wet soil', '["evergreen","wildlife_food","wet_tolerance","hedge"]', '["Sweetbay","Fothergilla","Ferns"]', 'Zone 2-3. Evergreen. Tolerates wet sites. Good hedge plant.', '{}', 'Native evergreen. Tolerates wet and dry. Good foundation plant.', '["Northeast","Mid_Atlantic"]', '5', '9', 30, 60, 1),

('ne-shrub-008', 'Sweet Pepperbush', 'Clethra alnifolia', 'shrub', 1, 3, 8, 6, 'Full sun to part shade', 'Wet to medium', 'Fragrant summer flowers, important late-season bee plant', '["bee_forage","fragrant","wet_tolerance","late_flowers"]', '["Winterberry","Swamp Azalea","Ferns"]', 'Zone 2-3. Blooms late summer when few others do. Fragrant. Tolerates wet soil.', '{}', 'Important late-season nectar. Very fragrant. Adaptable wet to average soil.', '["Northeast","Mid_Atlantic"]', '4', '9', 30, 65, 1),

('ne-shrub-009', 'Mountain Laurel', 'Kalmia latifolia', 'shrub', 1, 5, 10, 10, 'Part shade to full sun', 'Medium', 'Evergreen, showy flowers, needs acidic soil', '["evergreen","ornamental","pollinator_support","acidic_soil"]', '["Rhododendron","Blueberry","Hemlock"]', 'Zone 2-3. Requires acidic soil. Spectacular flowers. Evergreen cover.', '{}', 'Needs acidic soil. Beautiful flowers late spring. Poisonous - do not eat.', '["Northeast","Mid_Atlantic"]', '4', '9', 35, 60, 1),

('ne-shrub-010', 'Rhododendron (Rosebay)', 'Rhododendron maximum', 'shrub', 1, 5, 12, 12, 'Part shade', 'Medium', 'Large evergreen, showy flowers, prefers cool shade', '["evergreen","ornamental","wildlife_shelter","shade_tolerant"]', '["Hemlock","Mountain Laurel","Ferns"]', 'Zone 3-4. Prefers cool shaded sites. Evergreen shelter. Late bloomer.', '{}', 'Prefers cool mountain sites. Evergreen wildlife cover. Poisonous to livestock.', '["Northeast","Mid_Atlantic"]', '4', '8', 35, 65, 1),

('ne-shrub-011', 'Bayberry', 'Morella pensylvanica', 'shrub', 1, 3, 8, 8, 'Full sun', 'Dry to medium', 'Waxy berries, aromatic foliage, nitrogen fixer', '["nitrogen_fixer","aromatic","coastal_tolerant","wildlife_food"]', '["Beach Plum","Rosa Rugosa","Little Bluestem"]', 'Zone 3-4. Fixes nitrogen. Berries for candle wax. Salt tolerant.', '{"berries":"fall (wax for candles)"}', 'Nitrogen fixer. Very adaptable. Salt and drought tolerant. Berries yield bayberry wax.', '["Northeast","Mid_Atlantic"]', '3', '7', 25, 55, 1),

('ne-shrub-012', 'Beach Plum', 'Prunus maritima', 'shrub', 1, 3, 6, 6, 'Full sun', 'Dry to medium', 'Coastal native, edible plums, salt tolerant', '["edible_fruit","coastal_tolerant","wildlife_food","windbreak"]', '["Bayberry","Beach Rose","Beach Grass"]', 'Zone 3-4. Salt tolerant. Plums make excellent jelly.', '{"plums":"late summer"}', 'Very salt tolerant. Thrives in sandy coastal soils. Plums variable but make great jelly.', '["Northeast"]', '3', '7', 25, 50, 1),

('ne-shrub-013', 'Red Chokeberry', 'Aronia arbutifolia', 'shrub', 1, 3, 8, 5, 'Full sun to part shade', 'Wet to medium', 'Red berries persist, brilliant fall color, adaptable', '["edible_fruit","ornamental","fall_color","wet_tolerance"]', '["Highbush Blueberry","Winterberry","Ferns"]', 'Zone 2-3. Berries astringent raw, good cooked. Very adaptable.', '{"berries":"fall (best cooked)"}', 'Very adaptable. Tolerates wet soil. Berries persist for wildlife.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 25, 60, 1),

('ne-shrub-014', 'Black Chokeberry', 'Aronia melanocarpa', 'shrub', 1, 3, 6, 5, 'Full sun to part shade', 'Wet to medium', 'Black berries, high antioxidants, compact shrub', '["edible_fruit","medicinal","fall_color","wet_tolerance"]', '["Blueberry","Winterberry","Sumac"]', 'Zone 2-3. High antioxidant berries. Adaptable. Good fall color.', '{"berries":"fall"}', 'Compact shrub. High antioxidants. Tolerates wet or dry soil.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 25, 60, 1),

('ne-shrub-015', 'Serviceberry Shrub', 'Amelanchier alnifolia', 'shrub', 1, 2, 8, 6, 'Full sun to part shade', 'Medium', 'Shrub form, abundant sweet berries, early flowers', '["edible_fruit","pollinator_support","wildlife_food","spring_flowers"]', '["Hazelnut","Chokecherry","Currant"]', 'Zone 1-2. More compact than tree forms. Very productive.', '{"berries":"early summer"}', 'Western species but hardy East. Very productive. Sweet berries.', '["Northeast","Midwest"]', '2', '7', 20, 50, 1),

('ne-shrub-016', 'American Wild Rose', 'Rosa virginiana', 'shrub', 1, 2, 6, 4, 'Full sun to part shade', 'Medium', 'Fragrant pink flowers, rose hips, spreading by suckers', '["edible_fruit","pollinator_support","medicinal","thorny_barrier"]', '["Hawthorn","Elderberry","Blackberry"]', 'Zone 2-3. Hips high in vitamin C. Flowers for tea. Spreads by suckers.', '{"petals":"summer (tea)","hips":"fall (vitamin C)"}', 'Spreads readily. Rose hips excellent vitamin C source. Makes good hedge.', '["Northeast","Mid_Atlantic"]', '3', '8', 25, 55, 1),

('ne-shrub-017', 'Swamp Rose', 'Rosa palustris', 'shrub', 1, 2, 6, 4, 'Full sun to part shade', 'Wet to medium', 'Wetland rose, fragrant flowers, tolerates flooding', '["edible_fruit","pollinator_support","wet_tolerance","fragrant"]', '["Winterberry","Joe Pye Weed","Swamp Milkweed"]', 'Zone 2-3. Tolerates wet soil and flooding. Rose hips for tea.', '{"petals":"summer","hips":"fall"}', 'Best rose for wet sites. Tolerates occasional flooding.', '["Northeast","Mid_Atlantic"]', '4', '9', 30, 65, 1),

('ne-shrub-018', 'Staghorn Sumac', 'Rhus typhina', 'shrub', 1, 3, 15, 15, 'Full sun', 'Dry to medium', 'Fuzzy red berries, spectacular fall color, spreads by suckers', '["edible_fruit","fall_color","wildlife_food","erosion_control"]', '["Sassafras","Blackberry","Little Bluestem"]', 'Zone 3-4. Berries make pink lemonade. Brilliant fall color. Spreads aggressively.', '{"berries":"fall (pink lemonade)"}', 'Spreads aggressively by root suckers. Berries make tart drink. Great fall color.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 20, 50, 1),

('ne-shrub-019', 'Smooth Sumac', 'Rhus glabra', 'shrub', 1, 3, 10, 10, 'Full sun', 'Dry to medium', 'Similar to staghorn but smooth stems, berries for sumac-ade', '["edible_fruit","fall_color","wildlife_food","drought_tolerant"]', '["Sumac","Native Grasses","Butterfly Weed"]', 'Zone 3-4. More drought tolerant than staghorn. Berries for drinks.', '{"berries":"fall (drink)"}', 'Drought tolerant. Spreads but less than staghorn. Good for dry banks.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 15, 50, 1),

('ne-shrub-020', 'Fragrant Sumac', 'Rhus aromatica', 'shrub', 1, 3, 6, 10, 'Full sun', 'Dry to medium', 'Low spreading sumac, aromatic foliage, groundcover', '["groundcover","erosion_control","fragrant","drought_tolerant"]', '["Little Bluestem","Pasque Flower","Prairie Dropseed"]', 'Zone 3-4. Best sumac for erosion control. Aromatic leaves. Low spreader.', '{}', 'Best for erosion control. Low growing. Very drought tolerant.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 15, 45, 1),

('ne-shrub-021', 'Buttonbush', 'Cephalanthus occidentalis', 'shrub', 1, 3, 10, 8, 'Full sun', 'Wet', 'Round white flowers, wetland specialist, important bee plant', '["bee_forage","wildlife_food","wetland_plant","unique_flowers"]', '["Winterberry","Swamp Milkweed","Cardinal Flower"]', 'Zone 4-5. Requires wet soil. Spectacular round flowers. Important for bees and waterfowl.', '{}', 'Requires consistently wet soil. Unique spherical flowers. Excellent wildlife value.', '["Northeast","Mid_Atlantic","Midwest"]', '5', '10', 35, 70, 1),

('ne-shrub-022', 'Sweetfern', 'Comptonia peregrina', 'shrub', 1, 3, 4, 6, 'Full sun', 'Dry', 'Fern-like aromatic foliage, nitrogen fixer, dry site specialist', '["nitrogen_fixer","aromatic","erosion_control","drought_tolerant"]', '["Lowbush Blueberry","Bearberry","Little Bluestem"]', 'Zone 3-4. Fixes nitrogen. Very drought tolerant. Aromatic leaves for tea.', '{"leaves":"summer (tea)"}', 'Nitrogen fixer. Excellent for poor dry soils. Aromatic foliage.', '["Northeast"]', '2', '6', 20, 45, 1),

('ne-shrub-023', 'Leatherwood', 'Dirca palustris', 'shrub', 1, 5, 6, 6, 'Part shade', 'Wet to medium', 'Unique native, extremely flexible branches, early flowers', '["unique","early_flowers","wildlife","shade_tolerant"]', '["Spicebush","Jack-in-the-Pulpit","Ferns"]', 'Zone 2-3. Extremely flexible stems. Early spring flowers. Rare in cultivation.', '{}', 'Rare in cultivation. Unusual plant. Branches so flexible they tie in knots.', '["Northeast","Mid_Atlantic"]', '4', '9', 30, 60, 1),

('ne-shrub-024', 'Ninebark', 'Physocarpus opulifolius', 'shrub', 1, 3, 10, 10, 'Full sun to part shade', 'Medium', 'Exfoliating bark, white flowers, adaptable shrub', '["ornamental","erosion_control","wildlife","adaptable"]', '["Elderberry","Serviceberry","Native Grasses"]', 'Zone 2-3. Very adaptable. Peeling bark adds interest. Tolerates poor soil.', '{}', 'Very tough and adaptable. Interesting bark. Good for difficult sites.', '["Northeast","Mid_Atlantic","Midwest"]', '2', '8', 20, 55, 1),

('ne-shrub-025', 'Steeplebush', 'Spiraea tomentosa', 'shrub', 1, 3, 4, 3, 'Full sun', 'Wet to medium', 'Pink flower spikes, wet meadow specialist, spreads by suckers', '["pollinator_support","wet_tolerance","pioneer_species","ornamental"]', '["Joe Pye Weed","Swamp Milkweed","Ironweed"]', 'Zone 3-4. Tolerates wet meadows. Pink flower spikes late summer.', '{}', 'Good for wet meadow restoration. Spreads by suckers. Late summer bloom.', '["Northeast","Midwest"]', '4', '8', 30, 60, 1);

