INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-herb-001', 'Ramps / Wild Leek', 'Allium tricoccum', 'herbaceous', 1, 2, 1, 0.5, 'Part shade', 'Medium', 'Spring ephemeral, gourmet wild onion, slow to reproduce', '["edible_leaves","edible_bulbs","spring_ephemeral","gourmet"]', '["Trillium","Bloodroot","Trout Lily"]', 'Zone 1-2. Harvest sustainably (leaves only, not bulbs). Slow to reproduce.', '{"leaves":"spring","bulbs":"spring (harvest sustainably)"}', 'Overharvested in wild - grow your own. Takes 5-7 years from seed. Harvest leaves only.', '["Northeast","Mid_Atlantic"]', '3', '7', 30, 60, 1),

('ne-herb-002', 'Ostrich Fern', 'Matteuccia struthiopteris', 'herbaceous', 1, 2, 4, 3, 'Part shade', 'Medium to wet', 'Tall elegant fern, edible fiddleheads, spreads by rhizomes', '["edible_shoots","ornamental","erosion_control","shade_tolerant"]', '["Jack-in-the-Pulpit","Bloodroot","Foam Flower"]', 'Zone 2-3. Fiddleheads in spring. Harvest before fronds unfurl.', '{"fiddleheads":"spring"}', 'Spreads to form colonies. Fiddleheads must be cooked thoroughly. Beautiful fern.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '7', 30, 60, 1),

('ne-herb-003', 'Mayapple', 'Podophyllum peltatum', 'herbaceous', 1, 2, 1.5, 1.5, 'Part shade', 'Medium', 'Umbrella-like leaves, edible fruit when fully ripe, medicinal', '["edible_fruit","medicinal","groundcover","shade_tolerant"]', '["Trillium","Wild Ginger","Solomon Seal"]', 'Zone 2. Fruit edible ONLY when fully ripe. Rest of plant toxic. Spreads well.', '{"fruit":"late summer (only when very ripe)"}', 'Plant toxic except fully ripe fruit. Historically medicinal. Forms colonies.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 25, 60, 1),

('ne-herb-004', 'Wild Ginger', 'Asarum canadense', 'herbaceous', 1, 2, 0.5, 1, 'Shade', 'Medium', 'Heart-shaped leaves, ginger-scented roots, excellent groundcover', '["aromatic","groundcover","shade_tolerant","medicinal"]', '["Trillium","Jack-in-the-Pulpit","Bloodroot"]', 'Zone 1-2. Roots smell like ginger. Excellent dense groundcover for shade.', '{"roots":"(aromatic, not true ginger)"}', 'Not related to culinary ginger but aromatic. Excellent shade groundcover.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 30, 60, 1),

('ne-herb-005', 'Bloodroot', 'Sanguinaria canadensis', 'herbaceous', 1, 2, 0.5, 0.5, 'Part shade', 'Medium', 'White spring flowers, red sap, medicinal but toxic', '["spring_ephemeral","medicinal","ornamental","early_flowers"]', '["Trillium","Hepatica","Trout Lily"]', 'Zone 1-2. Beautiful early flowers. Red sap (toxic - external use only).', '{}', 'Beautiful but toxic. Red sap historically used as dye. Do not ingest.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 25, 60, 1),

('ne-herb-006', 'Trillium', 'Trillium grandiflorum', 'herbaceous', 1, 5, 1.5, 1, 'Part shade', 'Medium', 'Three-petaled white flowers, slow growing, woodland gem', '["ornamental","spring_ephemeral","wildlife","shade_tolerant"]', '["Bloodroot","Wild Ginger","Mayapple"]', 'Zone 2-3. Slow from seed (7+ years to flower). Do not pick flowers.', '{}', 'Very slow growing. Never pick wild trilliums. Buy nursery propagated only.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 30, 60, 1),

('ne-herb-007', 'Jack-in-the-Pulpit', 'Arisaema triphyllum', 'herbaceous', 1, 3, 2, 1, 'Shade', 'Medium', 'Unique hooded flower, red berries, shade specialist', '["ornamental","wildlife_food","shade_tolerant","unique"]', '["Ferns","Wild Ginger","Solomon Seal"]', 'Zone 2-3. Unique flower structure. Berries for wildlife. Very shade tolerant.', '{}', 'All parts toxic raw. Historically processed for food. Grow for interest, not food.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 30, 60, 1),

('ne-herb-008', 'Solomon Seal', 'Polygonatum biflorum', 'herbaceous', 1, 2, 3, 2, 'Part shade to shade', 'Medium', 'Arching stems, hanging white flowers, edible shoots', '["edible_shoots","ornamental","shade_tolerant","medicinal"]', '["Mayapple","Wild Ginger","Ferns"]', 'Zone 2-3. Young shoots edible (cooked). Graceful arching form.', '{"shoots":"spring (cooked)"}', 'Shoots similar to asparagus. Harvest sustainably. Beautiful shade plant.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 60, 1),

('ne-herb-009', 'False Solomon Seal', 'Maianthemum racemosum', 'herbaceous', 1, 2, 3, 2, 'Part shade to shade', 'Medium', 'Similar to Solomon Seal, flowers at tip, edible berries', '["edible_berries","ornamental","shade_tolerant","wildlife"]', '["Solomon Seal","Ferns","Wild Ginger"]', 'Zone 2-3. Berries edible (slightly bitter). Shoots edible young.', '{"shoots":"spring","berries":"fall (slightly bitter)"}', 'Similar growth to Solomon Seal. Berries okay raw but bitter. Better cooked.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 25, 60, 1),

('ne-herb-010', 'Wild Columbine', 'Aquilegia canadensis', 'herbaceous', 1, 2, 2, 1.5, 'Part shade to full sun', 'Medium', 'Red and yellow flowers, hummingbird magnet, self-sows', '["pollinator_support","hummingbird","ornamental","self_sowing"]', '["Wild Geranium","Spiderwort","Ferns"]', 'Zone 1-2. Excellent hummingbird plant. Self-sows readily.', '{}', 'Easy from seed. Hummingbird favorite. Self-sows freely.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 20, 60, 1),

('ne-herb-011', 'Wild Geranium', 'Geranium maculatum', 'herbaceous', 1, 2, 2, 1.5, 'Part shade to full sun', 'Medium', 'Pink flowers, medicinal astringent, easy to grow', '["pollinator_support","medicinal","ornamental","shade_tolerant"]', '["Columbine","Woodland Phlox","Ferns"]', 'Zone 1-2. Easy woodland plant. Astringent roots (medicinal).', '{}', 'Very easy to grow. Spreads slowly. Reliable bloomer.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 25, 60, 1),

('ne-herb-012', 'Bee Balm', 'Monarda didyma', 'herbaceous', 1, 1, 4, 2, 'Full sun to part shade', 'Medium', 'Red flowers, aromatic tea, attracts hummingbirds', '["bee_forage","hummingbird","aromatic_tea","medicinal"]', '["Joe Pye Weed","Coneflower","Black-Eyed Susan"]', 'Zone 1-2. Leaves for tea. Spreads vigorously. Hummingbird magnet.', '{"leaves":"summer (tea)","flowers":"summer (edible)"}', 'Spreads readily. Bergamot tea. Watch for powdery mildew in humid areas.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 25, 60, 1),

('ne-herb-013', 'Wild Bergamot', 'Monarda fistulosa', 'herbaceous', 1, 1, 3, 2, 'Full sun', 'Dry to medium', 'Lavender flowers, aromatic, more drought tolerant than bee balm', '["bee_forage","aromatic_tea","pollinator_support","drought_tolerant"]', '["Coneflower","Native Grasses","Butterfly Weed"]', 'Zone 1-2. More drought tolerant than bee balm. Excellent pollinator plant.', '{"leaves":"summer (tea)"}', 'Better for dry sites than bee balm. Less prone to mildew.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 20, 50, 1),

('ne-herb-014', 'Purple Coneflower', 'Echinacea purpurea', 'herbaceous', 1, 2, 4, 2, 'Full sun', 'Dry to medium', 'Purple daisy flowers, medicinal immune support, drought tolerant', '["pollinator_support","medicinal","drought_tolerant","ornamental"]', '["Black-Eyed Susan","Bee Balm","Native Grasses"]', 'Zone 1-2. Immune support herb. Drought tolerant. Long bloomer.', '{"roots":"fall (medicinal)","flowers":"summer (tea)"}', 'Popular immune herb. Easy from seed. Drought tolerant. Deadhead or let seed for birds.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 15, 50, 1),

('ne-herb-015', 'Black-Eyed Susan', 'Rudbeckia hirta', 'herbaceous', 1, 1, 3, 2, 'Full sun', 'Dry to medium', 'Cheerful yellow flowers, biennial/short-lived perennial, self-sows', '["pollinator_support","self_sowing","drought_tolerant","ornamental"]', '["Coneflower","Bee Balm","Little Bluestem"]', 'Zone 2-3. Self-sows abundantly. Long blooming. Drought tolerant.', '{}', 'Easy from seed. Self-sows readily. Can be weedy in gardens. Great for meadows.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 15, 50, 1),

('ne-herb-016', 'Joe Pye Weed', 'Eutrochium purpureum', 'herbaceous', 1, 2, 6, 3, 'Full sun to part shade', 'Wet to medium', 'Tall pink flowers, butterfly magnet, tolerates wet soil', '["pollinator_support","wet_tolerance","butterfly_magnet","ornamental"]', '["Swamp Milkweed","Ironweed","Cardinal Flower"]', 'Zone 2-3. Tolerates wet soil. Excellent butterfly plant. Tall back-of-border.', '{}', 'Very tall. Good for wet areas. Butterflies love it. Impressive mass plantings.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 30, 65, 1),

('ne-herb-017', 'New England Aster', 'Symphyotrichum novae-angliae', 'herbaceous', 1, 2, 5, 2, 'Full sun', 'Medium', 'Purple fall flowers, important late-season bee plant', '["pollinator_support","late_flowers","butterfly_host","fall_bloomer"]', '["Goldenrod","Joe Pye Weed","Native Grasses"]', 'Zone 2-3. Late season nectar critical for migrating monarchs. Tall and showy.', '{}', 'Blooms late summer to fall. Critical for late pollinators. Can get leggy - cut back mid-summer.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 25, 60, 1),

('ne-herb-018', 'Goldenrod', 'Solidago canadensis', 'herbaceous', 1, 2, 4, 2, 'Full sun', 'Dry to medium', 'Yellow fall flowers, important pollinator plant, spreads readily', '["pollinator_support","late_flowers","medicinal","pioneer_species"]', '["Aster","Black-Eyed Susan","Little Bluestem"]', 'Zone 2-3. NOT the allergy culprit (ragweed is). Excellent bee plant.', '{}', 'Spreads vigorously. Great for pollinators. Does NOT cause hay fever (that is ragweed).', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 20, 60, 1),

('ne-herb-019', 'Butterfly Weed', 'Asclepias tuberosa', 'herbaceous', 1, 2, 2, 1.5, 'Full sun', 'Dry', 'Orange flowers, monarch host, deep taproot, drought proof', '["pollinator_support","butterfly_host","drought_tolerant","monarch"]', '["Coneflower","Little Bluestem","Penstemon"]', 'Zone 1-2. Critical monarch host. Deep tap root = very drought tolerant.', '{}', 'Monarch caterpillar host. Very drought tolerant. Slow to emerge in spring. Hard to transplant.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 15, 45, 1),

('ne-herb-020', 'Swamp Milkweed', 'Asclepias incarnata', 'herbaceous', 1, 2, 4, 2, 'Full sun', 'Wet to medium', 'Pink flowers, monarch host, tolerates wet soil', '["pollinator_support","butterfly_host","wet_tolerance","monarch"]', '["Joe Pye Weed","Cardinal Flower","Ironweed"]', 'Zone 2-3. Monarch host for wet areas. Pink flowers fragrant.', '{}', 'Monarch caterpillar host. Tolerates wet soil. Easier to transplant than butterfly weed.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 25, 65, 1),

('ne-herb-021', 'Cardinal Flower', 'Lobelia cardinalis', 'herbaceous', 1, 2, 3, 1, 'Part shade to full sun', 'Wet', 'Brilliant red flowers, hummingbird magnet, requires wet soil', '["pollinator_support","hummingbird","wetland_plant","ornamental"]', '["Joe Pye Weed","Swamp Milkweed","Iris"]', 'Zone 2-3. Requires consistently wet soil. Spectacular red flowers for hummingbirds.', '{}', 'Short-lived but self-sows. Needs wet soil. Stunning red flowers.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 35, 70, 1),

('ne-herb-022', 'Christmas Fern', 'Polystichum acrostichoides', 'herbaceous', 1, 2, 2, 2, 'Part shade to shade', 'Medium', 'Evergreen fern, excellent groundcover, very hardy', '["evergreen","groundcover","shade_tolerant","erosion_control"]', '["Wild Ginger","Solomon Seal","Hepatica"]', 'Zone 2-3. Evergreen. One of easiest ferns. Good groundcover.', '{}', 'Very easy fern. Stays green all winter. Excellent shade groundcover.', '["Northeast","Mid_Atlantic"]', '3', '9', 25, 60, 1),

('ne-herb-023', 'Maidenhair Fern', 'Adiantum pedatum', 'herbaceous', 1, 2, 2, 2, 'Shade', 'Medium', 'Delicate black stems, fan-shaped fronds, elegant fern', '["ornamental","shade_tolerant","moisture_indicator","delicate"]', '["Bloodroot","Hepatica","Foam Flower"]', 'Zone 2-3. Indicates moist, rich soil. Very elegant fern.', '{}', 'Delicate appearance but hardy. Needs consistent moisture and shade.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 30, 60, 1),

('ne-herb-024', 'Common Milkweed', 'Asclepias syriaca', 'herbaceous', 1, 2, 4, 2, 'Full sun', 'Dry to medium', 'Pink flowers, critical monarch host, spreads by rhizomes', '["pollinator_support","butterfly_host","monarch","pioneer_species"]', '["Goldenrod","Aster","Native Grasses"]', 'Zone 2-3. Most important monarch host. Spreads aggressively. Young shoots edible (expert preparation).', '{"shoots":"spring (requires multiple boilings)"}', 'Critical monarch plant. Spreads vigorously by rhizomes. Young shoots edible with expert preparation.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 20, 60, 1),

('ne-herb-025', 'Little Bluestem', 'Schizachyrium scoparium', 'herbaceous', 1, 2, 3, 1.5, 'Full sun', 'Dry to medium', 'Native prairie grass, blue stems, copper fall color', '["ornamental","erosion_control","wildlife_cover","drought_tolerant"]', '["Coneflower","Butterfly Weed","Prairie Dropseed"]', 'Zone 3-4. Excellent native grass. Drought tolerant. Beautiful fall color.', '{}', 'Very drought tolerant. Beautiful fall color. Good wildlife cover. Native prairie grass.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 15, 45, 1);

-- ---------------------------------------------------------------------------
-- GROUNDCOVER LAYER (0-1 foot) - Low spreading plants
-- ---------------------------------------------------------------------------

INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-ground-001', 'Wintergreen', 'Gaultheria procumbens', 'groundcover', 1, 2, 0.5, 2, 'Part shade', 'Medium', 'Evergreen groundcover, edible berries, aromatic leaves', '["edible_fruit","aromatic","evergreen","groundcover"]', '["Blueberry","Bunchberry","Partridgeberry"]', 'Zone 1-2. Berries and leaves taste like wintergreen. Spreads slowly.', '{"berries":"fall/winter","leaves":"year-round (tea)"}', 'Source of natural wintergreen flavor. Needs acidic soil. Evergreen.', '["Northeast","Mid_Atlantic"]', '3', '8', 30, 60, 1),

('ne-ground-002', 'Bunchberry', 'Cornus canadensis', 'groundcover', 1, 2, 0.5, 1, 'Part shade', 'Medium', 'Miniature dogwood, edible berries, beautiful flowers', '["edible_fruit","ornamental","groundcover","shade_tolerant"]', '["Blueberry","Wintergreen","Ferns"]', 'Zone 1-2. Looks like tiny dogwood. Berries edible but mealy.', '{"berries":"fall"}', 'Beautiful groundcover for acidic shade. Slow to establish. Berries edible but bland.', '["Northeast"]', '2', '6', 35, 65, 1),

('ne-ground-003', 'Partridgeberry', 'Mitchella repens', 'groundcover', 1, 2, 0.25, 2, 'Shade', 'Medium', 'Evergreen trailing vine, red berries, paired flowers', '["edible_fruit","evergreen","groundcover","shade_tolerant"]', '["Wintergreen","Wild Ginger","Ferns"]', 'Zone 1-2. Evergreen trailer. Berries edible but tasteless.', '{"berries":"fall/winter"}', 'Beautiful evergreen groundcover. Berries persist. Edible but no flavor.', '["Northeast","Mid_Atlantic"]', '4', '9', 30, 60, 1),

('ne-ground-004', 'Bearberry', 'Arctostaphylos uva-ursi', 'groundcover', 1, 2, 0.5, 6, 'Full sun', 'Dry to medium', 'Evergreen groundcover, red berries, very tough', '["groundcover","evergreen","erosion_control","drought_tolerant"]', '["Lowbush Blueberry","Juniper","Little Bluestem"]', 'Zone 3-4. Extremely tough groundcover. Tolerates poor dry soil.', '{"berries":"fall (mealy, for wildlife)"}', 'Very tough. Good for banks and slopes. Evergreen. Drought tolerant once established.', '["Northeast","Midwest"]', '2', '6', 15, 40, 1),

('ne-ground-005', 'Wild Strawberry', 'Fragaria virginiana', 'groundcover', 1, 1, 0.5, 2, 'Full sun to part shade', 'Medium', 'Small intensely flavored berries, spreads by runners', '["edible_fruit","groundcover","pollinator_support","erosion_control"]', '["Violets","Clover","Native Grasses"]', 'Zone 1-2. Berries tiny but intensely flavored. Spreads as groundcover.', '{"berries":"early summer"}', 'Tiny berries pack incredible flavor. Spreads by runners. Parent of commercial strawberries.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '8', 20, 55, 1),

('ne-ground-006', 'Barren Strawberry', 'Waldsteinia fragarioides', 'groundcover', 1, 2, 0.5, 2, 'Part shade', 'Medium', 'Yellow flowers, strawberry-like leaves, excellent groundcover', '["groundcover","shade_tolerant","erosion_control","ornamental"]', '["Wild Ginger","Violets","Ferns"]', 'Zone 1-2. No fruit but excellent evergreen groundcover.', '{}', 'Better groundcover than true strawberry. Semi-evergreen. Tolerates shade.', '["Northeast","Mid_Atlantic"]', '4', '8', 25, 60, 1),

('ne-ground-007', 'Common Violet', 'Viola sororia', 'groundcover', 1, 1, 0.5, 1, 'Part shade to full sun', 'Medium', 'Edible flowers and leaves, self-sows readily, adaptable', '["edible_flowers","edible_greens","groundcover","self_sowing"]', '["Wild Strawberry","Clover","Native Grasses"]', 'Zone 1-2. Leaves and flowers edible in salads. Self-sows freely.', '{"leaves":"spring (salads)","flowers":"spring (edible/candied)"}', 'Leaves high in vitamins. Flowers beautiful in salads. Self-sows aggressively.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 20, 60, 1),

('ne-ground-008', 'Canada Mayflower', 'Maianthemum canadense', 'groundcover', 1, 2, 0.5, 2, 'Part shade to shade', 'Medium', 'Fragrant white flowers, spreads by rhizomes, shade groundcover', '["groundcover","shade_tolerant","fragrant","wildlife_fruit"]', '["Wild Ginger","Bunchberry","Ferns"]', 'Zone 2-3. Spreads to form colonies in shade. Fragrant flowers.', '{}', 'Excellent shade groundcover. Spreads by rhizomes. Tiny fragrant flowers.', '["Northeast","Midwest"]', '2', '7', 30, 60, 1),

('ne-ground-009', 'Foam Flower', 'Tiarella cordifolia', 'groundcover', 1, 2, 1, 1.5, 'Part shade to shade', 'Medium', 'Foamy white flowers, evergreen leaves, native shade plant', '["groundcover","shade_tolerant","ornamental","evergreen"]', '["Wild Ginger","Hepatica","Ferns"]', 'Zone 2-3. Beautiful spring flowers. Evergreen leaves. Excellent groundcover.', '{}', 'Beautiful native groundcover. Foamy white flowers in spring. Semi-evergreen.', '["Northeast","Mid_Atlantic"]', '3', '8', 30, 60, 1),

('ne-ground-010', 'Pennsylvania Sedge', 'Carex pensylvanica', 'groundcover', 1, 1, 0.75, 1, 'Part shade to shade', 'Dry to medium', 'Native sedge, lawn alternative, drought tolerant', '["groundcover","shade_tolerant","drought_tolerant","lawn_alternative"]', '["Wild Ginger","Hepatica","Native Trees"]', 'Zone 2-3. Excellent lawn alternative for shade. No mowing needed. Drought tolerant.', '{}', 'Best native lawn alternative. Tolerates shade and drought. Low maintenance.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 20, 50, 1);

