INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-vine-001', 'Wild Grape', 'Vitis riparia', 'vine', 1, 3, 40, 20, 'Full sun to part shade', 'Medium', 'Native grape, edible fruit, aggressive climber, wildlife food', '["edible_fruit","wildlife_food","winemaking","aggressive"]', '["Black Walnut","Sassafras","Native Trees"]', 'Zone 4-5. Grapes tart but good for jelly/wine. Very aggressive - needs management.', '{"grapes":"fall","leaves":"spring (stuffed grape leaves)"}', 'Very aggressive. Good fruit but needs heavy management. Parent of many grape varieties.', '["Northeast","Mid_Atlantic","Midwest"]', '2', '8', 25, 60, 1),

('ne-vine-002', 'Virginia Creeper', 'Parthenocissus quinquefolia', 'vine', 1, 2, 50, 30, 'Full sun to shade', 'Medium', 'Five-leafed climber, brilliant fall color, fast growing', '["ornamental","fall_color","wildlife_food","erosion_control"]', '["Native Trees","Fence","Building"]', 'Zone 5. Spectacular fall color. Berries for wildlife. Can damage buildings.', '{}', 'Beautiful fall color. Good for fences. Berries toxic to humans. Aggressive.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '10', 20, 65, 1),

('ne-vine-003', 'American Bittersweet', 'Celastrus scandens', 'vine', 1, 3, 30, 15, 'Full sun to part shade', 'Medium', 'Orange/red berries, ornamental, needs both sexes', '["ornamental","wildlife_food","winter_interest","aggressive"]', '["Native Trees","Fence Posts"]', 'Zone 4-5. Need male and female for berries. Can strangle small trees.', '{}', 'Beautiful orange berries. Need male and female. Aggressive - monitor closely.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 25, 55, 1),

('ne-vine-004', 'Trumpet Honeysuckle', 'Lonicera sempervirens', 'vine', 1, 2, 20, 10, 'Full sun to part shade', 'Medium', 'Red tubular flowers, hummingbird magnet, native honeysuckle', '["pollinator_support","hummingbird","ornamental","non_invasive"]', '["Clematis","Native Trees","Fence"]', 'Zone 3-4. Native honeysuckle (unlike invasive Asian species). Hummingbird favorite.', '{}', 'Non-invasive native honeysuckle. Hummingbirds love it. Long blooming period.', '["Northeast","Mid_Atlantic","Southeast"]', '4', '9', 25, 55, 1),

('ne-vine-005', 'Dutchman Pipe', 'Aristolochia macrophylla', 'vine', 1, 3, 30, 20, 'Part shade to full sun', 'Medium', 'Large heart-shaped leaves, unique pipe-shaped flowers', '["ornamental","dense_shade","butterfly_host","unique"]', '["Native Trees","Arbor","Pergola"]', 'Zone 4-5. Creates dense shade. Host plant for pipevine swallowtail.', '{}', 'Excellent arbor vine. Dense shade. Host for beautiful pipevine swallowtail butterfly.', '["Northeast","Mid_Atlantic"]', '4', '8', 30, 60, 1);

