INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-root-001', 'Jerusalem Artichoke', 'Helianthus tuberosus', 'root', 1, 1, 10, 4, 'Full sun', 'Medium', 'Tall sunflower with edible tubers, very productive, spreads readily', '["edible_tubers","pollinator_support","windbreak","pioneer_species"]', '["Three Sisters","Comfrey","Native Grasses"]', 'Zone 4-5. Tubers very productive. Plant where spreading is okay. Harvest as needed.', '{"tubers":"fall through spring"}', 'Also called sunchoke. Very productive. Spreads aggressively. Harvest tubers all winter.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 20, 55, 1),

('ne-root-002', 'Groundnut', 'Apios americana', 'root', 1, 2, 6, 3, 'Part shade to full sun', 'Wet to medium', 'Nitrogen-fixing vine, edible tubers, fragrant flowers', '["edible_tubers","nitrogen_fixer","pollinator_support","vining"]', '["Wild Grape","Native Trees","Wet Areas"]', 'Zone 4-5. Tubers nutty flavored. Nitrogen fixer. Historically important food.', '{"tubers":"fall"}', 'Nitrogen-fixing tuber crop. Historically important Native American food. Vining habit.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 30, 65, 1),

('ne-root-003', 'Wild Garlic', 'Allium canadense', 'root', 1, 1, 1.5, 0.5, 'Full sun to part shade', 'Medium', 'Small native garlic, edible bulbs and greens, naturalizes', '["edible_bulbs","edible_greens","naturalizing","culinary"]', '["Native Wildflowers","Meadow Plants"]', 'Zone 1-2. Bulbs and greens have mild garlic flavor. Naturalizes in lawns.', '{"bulbs":"spring/fall","greens":"spring"}', 'Mild garlic flavor. Easy to grow. Naturalizes readily. All parts edible.', '["Northeast","Mid_Atlantic","Midwest"]', '4', '9', 20, 60, 1);

