INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('ne-aquatic-001', 'Pickerelweed', 'Pontederia cordata', 'aquatic', 1, 1, 3, 2, 'Full sun', 'Aquatic', 'Blue flower spikes, emergent aquatic, edible seeds', '["edible_seeds","pollinator_support","wetland_plant","water_filtration"]', '["Cattail","Arrowhead","Water Lily"]', 'Zone 5. Shallow water (0-12 inches). Blue flowers. Seeds edible.', '{"seeds":"late summer"}', 'Beautiful pond plant. Seeds edible (grain-like). Good water purifier.', '["Northeast","Mid_Atlantic","Southeast","Midwest"]', '3', '10', 30, 70, 1),

('ne-aquatic-002', 'Arrowhead', 'Sagittaria latifolia', 'aquatic', 1, 1, 2, 1, 'Full sun', 'Aquatic', 'Arrow-shaped leaves, white flowers, edible tubers', '["edible_tubers","pollinator_support","wetland_plant","waterfowl_food"]', '["Pickerelweed","Cattail","Rice Cutgrass"]', 'Zone 5. Tubers called "duck potato" - important waterfowl food.', '{"tubers":"fall"}', 'Edible tubers (duck potato). Important food plant. Shallow water or mud.', '["Northeast","Mid_Atlantic","Midwest"]', '5', '11', 30, 70, 1);


