INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('mw-canopy-001', 'Sugar Maple', 'Acer saccharum', 'canopy', 1, 30, 75, 50, 'Full sun to part shade', 'Medium', 'Iconic fall color, best syrup production, long-lived', '["sap_production","fall_color","shade_provider","timber_value"]', '["Ironwood","Trillium","Wild Ginger"]', 'Zone 2-3. Best maple for syrup. Spectacular fall color. Very long-lived.', '{"sap":"late winter to early spring"}', 'Best species for maple syrup. Beautiful fall color. Slow growing but very long-lived.', '["Northeast","Midwest"]', '3', '8', 25, 55, 1),

('mw-canopy-002', 'White Oak', 'Quercus alba', 'canopy', 1, 30, 80, 80, 'Full sun', 'Medium', 'Majestic long-lived oak, sweet acorns, valuable timber', '["acorn_production","wildlife_food","timber_value","long_lived"]', '["Hazelnut","Serviceberry","Wild Ginger"]', 'Zone 2-3. Slow growing but very long-lived. Sweeter acorns than red oak.', '{"acorns":"fall (less tannin than red oaks)"}', 'Slower than red oak but longer-lived. Best acorns for eating. Extremely valuable timber.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '9', 30, 60, 1),

('mw-canopy-003', 'Northern Red Oak', 'Quercus rubra', 'canopy', 1, 25, 75, 60, 'Full sun', 'Medium', 'Fast-growing oak, fall color, important wildlife tree', '["acorn_production","wildlife_habitat","shade_provider","fall_color"]', '["American Hazelnut","Serviceberry","Wild Ginger"]', 'Zone 2-3. Faster growing than white oak. Good for establishing food forests.', '{"acorns":"fall (after leaching)"}', 'Faster growing than white oak. Acorns need leaching. Beautiful fall color.', '["Northeast","Mid_Atlantic","Midwest"]', '3', '8', 30, 60, 1),

('mw-canopy-004', 'Hackberry', 'Celtis occidentalis', 'canopy', 1, 20, 60, 50, 'Full sun', 'Dry to medium', 'Adaptable urban tree, sweet berries, drought tolerant', '["edible_fruit","wildlife_food","drought_tolerant","urban_tolerant"]', '["Elm","Redbud","Native Grasses"]', 'Zone 3-4. Very adaptable. Sweet berries loved by birds. Tolerates urban conditions.', '{"berries":"fall"}', 'Extremely adaptable. Tolerates poor soil and urban conditions. Sweet berries.', '["Midwest","Northeast"]', '3', '9', 15, 50, 1),

('mw-canopy-005', 'Cottonwood', 'Populus deltoides', 'canopy', 1, 15, 80, 60, 'Full sun', 'Wet to medium', 'Very fast growth, wetland specialist, biomass production', '["fast_growth","biomass_production","riparian_buffer","wildlife_cavities"]', '["Willow","Elderberry","Swamp Milkweed"]', 'Zone 4-5. Fastest native tree. Good for wet areas needing quick shade.', '{}', 'Extremely fast growing. Good for wet areas and biomass. Weak wood (drop branches).', '["Midwest","Northeast"]', '2', '9', 25, 60, 1),

('mw-canopy-006', 'Kentucky Coffeetree', 'Gymnocladus dioicus', 'canopy', 1, 20, 70, 50, 'Full sun', 'Medium', 'Unique compound leaves, nitrogen-friendly, drought tolerant', '["nitrogen_friendly","drought_tolerant","unique_form","urban_tolerant"]', '["Redbud","Prairie Plants","Native Grasses"]', 'Zone 3-4. Tolerates poor dry soil. Leaf litter improves soil. Drought proof.', '{"seeds":"(roasted as coffee substitute - caution)"}', 'Very drought tolerant. Nitrogen-rich leaf litter. Seeds historically roasted as coffee.', '["Midwest"]', '3', '8', 15, 45, 1),

('mw-canopy-007', 'Pecan', 'Carya illinoinensis', 'canopy', 1, 10, 100, 70, 'Full sun', 'Medium to wet', 'Large delicious nuts, massive size, commercial potential', '["edible_nuts","wildlife_food","shade_provider","commercial_crop"]', '["Black Walnut","Persimmon","Native Grasses"]', 'Zone 3-4. Northern varieties available. Huge trees. Excellent nuts.', '{"nuts":"fall"}', 'Choose northern varieties. Very large tree. Excellent nuts. Some varieties self-fertile.', '["Midwest","Southeast"]', '5', '9', 30, 60, 1),

('mw-canopy-008', 'River Birch', 'Betula nigra', 'canopy', 1, 15, 60, 40, 'Full sun', 'Wet to medium', 'Exfoliating bark, tolerates wet sites, fast growing', '["ornamental","wet_tolerance","erosion_control","sap_production"]', '["Elderberry","Winterberry","Ferns"]', 'Zone 3-4. Best birch for wet sites. Beautiful peeling bark. More disease resistant.', '{"sap":"early spring"}', 'Best birch for wet sites. More resistant to bronze birch borer. Beautiful bark.', '["Midwest","Mid_Atlantic","Southeast"]', '4', '9', 30, 65, 1),

('mw-canopy-009', 'Northern Catalpa', 'Catalpa speciosa', 'canopy', 1, 15, 60, 40, 'Full sun', 'Medium', 'Large leaves, showy flowers, extremely rot-resistant wood', '["bee_forage","rot_resistant_wood","fast_growth","unique"]', '["Elderberry","Native Grasses","Comfrey"]', 'Zone 3-4. Rot-resistant wood (fence posts). Large tropical-looking leaves.', '{}', 'Extremely rot-resistant wood. Fast growing. Large leaves create dense shade.', '["Midwest"]', '4', '8', 20, 50, 1),

('mw-canopy-010', 'Swamp White Oak', 'Quercus bicolor', 'canopy', 1, 25, 60, 60, 'Full sun', 'Wet to medium', 'Tolerates wet soil, sweet acorns, good fall color', '["acorn_production","wet_tolerance","wildlife_food","fall_color"]', '["Elderberry","Winterberry","Native Grasses"]', 'Zone 2-3. Best oak for wet sites. Sweeter acorns than red oaks.', '{"acorns":"fall"}', 'Best oak for wet areas. Acorns relatively sweet. Adapts to various conditions.', '["Midwest","Northeast"]', '3', '8', 30, 60, 1);

--  UNDERSTORY, SHRUB, HERBACEOUS (Midwest) - Key species for comprehensive coverage

INSERT INTO species (id, common_name, scientific_name, layer, is_native, years_to_maturity, mature_height_ft, mature_width_ft, sun_requirements, water_requirements, description, permaculture_functions, companion_plants, zone_placement_notes, edible_parts, sourcing_notes, broad_regions, min_hardiness_zone, max_hardiness_zone, min_rainfall_inches, max_rainfall_inches, ai_generated) VALUES
('mw-understory-001', 'Redbud', 'Cercis canadensis', 'understory', 1, 5, 20, 25, 'Full sun to part shade', 'Medium', 'Pink spring flowers, heart-shaped leaves, early bee plant', '["pollinator_support","ornamental","spring_flowers","edible_flowers"]', '["Dogwood","Spicebush","Wild Ginger"]', 'Zone 2-3. Very early flowers (before leaves). Beautiful pink blooms.', '{"flowers":"spring (edible)"}', 'One of earliest native bloomers. Important early nectar source. Beautiful.', '["Midwest","Mid_Atlantic","Southeast"]', '4', '9', 25, 55, 1),

('mw-shrub-001', 'American Plum', 'Prunus americana', 'shrub', 1, 3, 15, 15, 'Full sun', 'Medium', 'White spring flowers, red-yellow plums, forms thickets', '["edible_fruit","pollinator_support","wildlife_food","windbreak"]', '["Hazelnut","Chokecherry","Native Grasses"]', 'Zone 2-3. Spreads by suckers. Plums variable quality. Beautiful flowers.', '{"fruit":"late summer"}', 'Spreads to form thickets. Variable fruit quality. Makes good jam. Beautiful flowers.', '["Midwest"]', '3', '8', 20, 50, 1),

('mw-shrub-002', 'Gray Dogwood', 'Cornus racemosa', 'shrub', 1, 3, 10, 10, 'Full sun to part shade', 'Medium', 'White berries on red stems, spreads by suckers, adaptable', '["wildlife_food","ornamental","erosion_control","adaptable"]', '["Elderberry","Sumac","Native Grasses"]', 'Zone 2-3. Spreads by suckers. White berries for wildlife. Adaptable.', '{}', 'Very adaptable. Forms colonies. Important wildlife food. White berries striking.', '["Midwest","Northeast"]', '3', '8', 20, 50, 1),

('mw-shrub-003', 'Lead Plant', 'Amorpha canescens', 'shrub', 1, 3, 3, 3, 'Full sun', 'Dry', 'Purple flower spikes, nitrogen fixer, prairie specialist', '["nitrogen_fixer","pollinator_support","drought_tolerant","prairie_plant"]', '["Big Bluestem","Prairie Dropseed","Coneflower"]', 'Zone 3-4. Deep tap root. Excellent nitrogen fixer. True prairie plant.', '{}', 'Fixes nitrogen. Deep tap root = drought proof. Important prairie restoration plant.', '["Midwest"]', '2', '7', 15, 40, 1),

('mw-shrub-004', 'New Jersey Tea', 'Ceanothus americanus', 'shrub', 1, 3, 3, 4, 'Full sun to part shade', 'Dry to medium', 'White flower clusters, nitrogen fixer, drought tolerant', '["nitrogen_fixer","pollinator_support","drought_tolerant","medicinal"]', '["Butterfly Weed","Little Bluestem","Hazelnut"]', 'Zone 3-4. Fixes nitrogen. Roots make caffeine-free tea. Very drought tolerant.', '{"leaves":"(tea substitute, historical)"}', 'Nitrogen fixer. Roots historically used as tea during Revolution. Drought tolerant.', '["Midwest","Northeast"]', '4', '8', 15, 50, 1),

('mw-herb-001', 'Big Bluestem', 'Andropogon gerardii', 'herbaceous', 1, 2, 6, 2, 'Full sun', 'Dry to medium', 'Tall prairie grass, deep roots, important biomass', '["biomass","erosion_control","wildlife_cover","prairie_keystone"]', '["Coneflower","Prairie Blazing Star","Rattlesnake Master"]', 'Zone 3-4. King of prairie grasses. Roots 12+ feet deep. Carbon sequestration champion.', '{}', 'Most important tall grass prairie species. Roots incredibly deep. Essential prairie grass.', '["Midwest"]', '4', '9', 15, 50, 1),

('mw-herb-002', 'Prairie Blazing Star', 'Liatris pycnostachya', 'herbaceous', 1, 2, 4, 1.5, 'Full sun', 'Dry to medium', 'Purple spikes blooming top-down, monarch magnet, prairie native', '["pollinator_support","butterfly_magnet","prairie_plant","drought_tolerant"]', '["Big Bluestem","Coneflower","Milkweed"]', 'Zone 2-3. Blooms top-down (unique). Critical monarch nectar.', '{}', 'Unique top-down bloom. Monarchs love it. Deep tap root = drought tolerant.', '["Midwest"]', '3', '9', 15, 45, 1),

('mw-herb-003', 'Compass Plant', 'Silphium laciniatum', 'herbaceous', 1, 3, 8, 2, 'Full sun', 'Dry to medium', 'Huge yellow flowers, leaves align north-south, deep tap root', '["pollinator_support","drought_tolerant","prairie_keystone","unique"]', '["Big Bluestem","Cup Plant","Prairie Blazing Star"]', 'Zone 3-4. Leaves point north-south. Root 15+ feet deep. Dramatic prairie plant.', '{"sap":"(chewing gum substitute, historical)"}', 'Spectacular plant. Root incredibly deep. Leaves compass-aligned. Slow from seed.', '["Midwest"]', '3', '9', 15, 45, 1),

('mw-herb-004', 'Cup Plant', 'Silphium perfoliatum', 'herbaceous', 1, 2, 7, 3, 'Full sun', 'Medium to wet', 'Leaves form cups, yellow flowers, important water source', '["pollinator_support","wildlife_water","biomass","unique"]', '["Joe Pye Weed","Swamp Milkweed","Big Bluestem"]', 'Zone 2-3. Leaves catch water for birds/insects. Tall and productive.', '{}', 'Leaves form cups that hold water. Important for pollinators. Tall and showy.', '["Midwest"]', '4', '9', 25, 60, 1),

('mw-herb-005', 'Prairie Dropseed', 'Sporobolus heterolepis', 'herbaceous', 1, 2, 3, 2, 'Full sun', 'Dry to medium', 'Fine-textured grass, fragrant flowers, beautiful fall color', '["ornamental","erosion_control","fragrant","drought_tolerant"]', '["Little Bluestem","Coneflower","Butterfly Weed"]', 'Zone 3-4. Flowers smell like coriander. Beautiful fine texture. Excellent ornamental.', '{}', 'Most ornamental native grass. Flowers fragrant. Beautiful arching form. Drought tolerant.', '["Midwest"]', '3', '9', 15, 45, 1),

('mw-ground-001', 'Wild Ginger (Midwest)', 'Asarum canadense', 'groundcover', 1, 2, 0.5, 1, 'Shade', 'Medium', 'Heart-shaped leaves, aromatic roots, excellent groundcover', '["aromatic","groundcover","shade_tolerant","medicinal"]', '["Trillium","Jack-in-the-Pulpit","Bloodroot"]', 'Zone 1-2. Same as Northeast - excellent shade groundcover.', '{"roots":"(aromatic, not true ginger)"}', 'Excellent shade groundcover. Aromatic roots. Spreads slowly.', '["Midwest","Northeast","Mid_Atlantic"]', '3', '8', 30, 60, 1);


